    color: var(--portfolio-text-primary);
  }

  .text-portfolio-secondary {
    color: var(--portfolio-text-secondary);
  }

  .bg-portfolio-primary {
    background-color: var(--portfolio-primary);
  }

  .bg-portfolio-secondary {
    background-color: var(--portfolio-secondary);
  }

  .bg-portfolio-light {
    background-color: var(--portfolio-bg-light);
  }
}

@layer utilities {
  /* Animation keyframes */
  @keyframes gradientShift {
    0% {
      background-position: 0% 50%;
    }
    50% {
      background-position: 100% 50%;
    }
    100% {
      background-position: 0% 50%;
    }
  }

  @keyframes float {
    0%, 100% {
      transform: translateY(0px);
    }
    50% {
      transform: translateY(-10px);
    }
  }

  @keyframes pulse-glow {
    0%, 100% {
      filter: drop-shadow(0 0 20px rgba(139, 92, 246, 0.5));
    }
    50% {
      filter: drop-shadow(0 0 30px rgba(139, 92, 246, 0.8));
    }
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .animate-pulse-glow {
    animation: pulse-glow 3s ease-in-out infinite;
  }
}

@layer components {
  .hero-gradient {
    background: var(--portfolio-gradient);
    position: relative;
    overflow: hidden;
  }

  .hero-gradient::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iNDAiIGhlaWdodD0iNDAiIHZpZXdCb3g9IjAgMCA0MCA0MCIgZmlsbD0ibm9uZSIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj4KPGNpcmNsZSBjeD0iMjAiIGN5PSIyMCIgcj0iMSIgZmlsbD0id2hpdGUiIGZpbGwtb3BhY2l0eT0iMC4xIi8+Cjwvc3ZnPgo=');
    opacity: 0.3;
    animation: float 6s ease-in-out infinite;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-20px); }
  }

  .glass-card {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
  }

  .card-shadow {
    box-shadow: 0 4px 24px var(--portfolio-card-shadow);
    transition: all 0.3s ease;
  }

  .card-shadow:hover {
    box-shadow: 0 8px 40px rgba(139, 92, 246, 0.3);
    transform: translateY(-2px);
  }

  .skill-tag {
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
  }

  .skill-tag::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.4), transparent);
    transition: left 0.5s;
  }

  .skill-tag:hover::before {
    left: 100%;
  }

  .skill-tag:hover {
    transform: translateY(-4px) scale(1.05);
    box-shadow: 0 10px 30px rgba(139, 92, 246, 0.3);
  }

  .project-card {
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    position: relative;
    overflow: hidden;
  }

  .project-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: var(--portfolio-gradient);
    opacity: 0;
    transition: opacity 0.3s ease;
    z-index: 1;
  }

  .project-card:hover::before {
    opacity: 0.05;
  }

  .project-card:hover {
    transform: translateY(-8px) rotateX(2deg);
    box-shadow: 0 20px 40px rgba(139, 92, 246, 0.3);
  }

  .timeline-line::before {
    content: '';
    position: absolute;
    left: 19px;
    top: 60px;
    bottom: -20px;
    width: 3px;
    background: linear-gradient(180deg, var(--portfolio-primary), var(--portfolio-secondary));
    border-radius: 2px;
  }

  .timeline-dot {
    width: 50px;
    height: 50px;
    border-radius: 50%;
    background: var(--portfolio-gradient);
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-weight: 700;
    position: relative;
    z-index: 10;
    box-shadow: 0 4px 20px rgba(139, 92, 246, 0.4);
    animation: pulse 2s infinite;
  }

  @keyframes pulse {
    0%, 100% { box-shadow: 0 4px 20px rgba(139, 92, 246, 0.4); }
    50% { box-shadow: 0 4px 30px rgba(139, 92, 246, 0.6); }
  }

  .nav-link {
    position: relative;
    transition: all 0.3s ease;
    font-weight: 500;
  }

  .nav-link::after {
    content: '';
    position: absolute;
    bottom: -6px;
    left: 50%;
    transform: translateX(-50%);
    width: 0;
    height: 3px;
    background: var(--portfolio-gradient);
    border-radius: 2px;
    transition: width 0.3s ease;
  }

  .nav-link:hover::after,
  .nav-link.active::after {
    width: 100%;
  }

  .nav-link:hover {
    color: var(--portfolio-primary);
    transform: translateY(-1px);
  }

  .stat-card {
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.9) 0%, rgba(248, 250, 252, 0.8) 100%);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(139, 92, 246, 0.1);
    position: relative;
    overflow: hidden;
  }

  .stat-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 3px;
    background: var(--portfolio-gradient);
  }

  .animated-gradient {
    background: var(--portfolio-gradient);
    background-size: 200% 200%;
    animation: gradientShift 3s ease infinite;
  }

  @keyframes gradientShift {
    0%, 100% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
  }

  .text-gradient {
    background: var(--portfolio-gradient);
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-size: 200% 200%;
    animation: gradientShift 3s ease infinite;
  }

  .floating-element {
    animation: float 6s ease-in-out infinite;
  }

  .glow-effect {
    filter: drop-shadow(0 0 20px rgba(139, 92, 246, 0.5));
  }
}

@layer utilities {
  .text-portfolio-primary {
    color: var(--portfolio-text-primary);
  }

  .text-portfolio-secondary {
    color: var(--portfolio-text-secondary);
  }

  .bg-portfolio-light {
    background-color: var(--portfolio-bg-light);
  }
}

