                          className="text-3xl font-bold text-gradient mb-2"
                          initial={{ scale: 0 }}
                          whileInView={{ scale: 1 }}
                          viewport={{ once: true }}
                          transition={{ 
                            type: "spring",
                            stiffness: 300,
                            delay: index * 0.1 + 1
                          }}
                        >
                          {stat.value}
                        </motion.div>
                        <div className="text-portfolio-secondary font-medium">{stat.label}</div>
                      </CardContent>
                    </Card>
                  </motion.div>
                );
              })}
            </div>
          </FadeIn>
        </div>
      </div>
    </SectionWrapper>
  );
}

